    public int nearestExit(char[][] maze, int[] entrance) {
        // think of maze as graph where two cells are connected they are adjacent and
        // both empty
        // we can find the minium path to the exit by BFS from the entrance - must keep
        // track of visited
        // cells
        // To return the length of shortest path, we need to know the level of the BFS,
        // so we will iterate
        // by levels in the BFS using a null marker and keep a depth counter.

        int depth = 0;
        int m = maze.length;
        int n = maze[0].length;
        boolean[][] visited = new boolean[m][n];
        int sr = entrance[0];
        int sc = entrance[1];
        Deque<Cell> toVisit = new LinkedList<>();
        toVisit.offer(new Cell(sr, sc));
        toVisit.offer(null); // offer a null marker for the end of the level
        while (!toVisit.isEmpty()) {
            Cell v = toVisit.poll();
            while (v != null) {
                visited[v.r][v.c] = true;
                // check we got to an exit and return
                if ((v.c == 0 || v.c == n - 1 || v.r == 0 || v.r == m - 1)
                        && (v.c != sc || v.r != sr)) {
                    return depth;
                }
                // otherwise, enqueue adjacent empty cells and continue
                // left
                Cell adj = new Cell(v.r, v.c - 1);
                if (v.c > 0 && emptyNotVisited(adj, maze, visited)) {
                    if (isExit(adj, sc, sr, maze)) {
                        return depth+1;
                    }
                    toVisit.offer(adj);
                }
                // right
                adj = new Cell(v.r, v.c + 1);
                if (v.c < n - 1 && emptyNotVisited(adj, maze, visited)) {
                    if (isExit(adj, sc, sr, maze)) {
                        return depth+1;
                    }
                    toVisit.offer(adj);
                }
                // up
                adj = new Cell(v.r-1, v.c);
                if (v.r > 0 && emptyNotVisited(adj, maze, visited)) {
                    if (isExit(adj, sc, sr, maze)) {
                        return depth+1;
                    }
                    toVisit.offer(adj);
                }
                // down
                adj = new Cell(v.r+1, v.c);
                if (v.r < m - 1 && emptyNotVisited(adj, maze, visited)) {
                    if (isExit(adj, sc, sr, maze)) {
                        return depth+1;
                    }
                    toVisit.offer(adj);
                }
                v = toVisit.poll();
            }
            // got a null marker, so increment to the next level depth and add a new marker
            depth++;
            System.out.println(String.format("depth: %d, count: %d", depth, toVisit.size()));
            if (!toVisit.isEmpty()) {
                toVisit.offer(null);
            }
        }
        // didn't find an exit, so return -1
        return -1;
    }

    boolean emptyNotVisited(Cell c, char[][] maze, boolean[][] visited) {
        return maze[c.r][c.c] == '.' && !visited[c.r][c.c];
    }
    boolean isExit(Cell v, int sc, int sr, char[][] maze) {
        return (v.c == 0 || v.c == maze[0].length - 1 || v.r == 0 || v.r == maze.length - 1)
                        && (v.c != sc || v.r != sr);
    }

    static class Cell {
        int r;
        int c;

        Cell(int r, int c) {
            this.r = r;
            this.c = c;
        }

        public String toString() {
            return "(" + r + ", " + c + ")";
        }
    }
